package com.Casting;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 200;
		
		// casting
		short s = (short)i;
		System.out.println(s);
		
		double d = (double)i;
		System.out.println(d);

		byte b = (byte)i;
		System.out.println(b);
	}

}
